    private void okokMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_okokMouseClicked
        String iname=tname.getText();
        int ilcd=Integer.parseInt(tlcd.getText());
        int iram=Integer.parseInt(tram.getText());
        int imouse=Integer.parseInt(tmouse.getText());
        
        boolean imember = tmember.isSelected();
        
        Ja1 it1=new Ja1(iname,ilcd,iram,imouse,imember);

        
        list.setText(it1.show());

        
    }//GEN-LAST:event_okokMouseClicked

