       int ipay=Integer.parseInt(tpay.getText()); 
       String iname=tname.getText();
        int ilcd=Integer.parseInt(tlcd.getText());
        int iram=Integer.parseInt(tram.getText());
        int imouse=Integer.parseInt(tmouse.getText());
